    def get_balanceOf(self, contract, validate=None):
        if validate:
            decimals = int(contract.functions.decimals().call())
            if decimals != 18:
                missing_decimals = 18 - int(decimals)
            else:
                missing_decimals = 0

            return int(str(contract.functions.balanceOf(
                self.web3.to_checksum_address(self.address),
            ).call()) + missing_decimals * '0')
        else:
            return int(str(contract.functions.balanceOf(
                self.web3.to_checksum_address(self.address),
            ).call()))

